		void Insert(unsigned pos, char c)
		{
			if (pos > length_)
				pos = length_;

			if (pos == length_)
				(*this) += c;
			else
			{
				unsigned oldLength = length_;
				Resize(length_ + 1);
				MoveRange(pos + 1, pos, oldLength - pos);
				buffer_[pos] = c;
			}
		}

